(function () {
'use strict';
var module = angular.module('fim.base');
module.controller('SettingsPluginFullNodeController', function($scope, serverService, $timeout, $stateParams, settings) {
  
  $scope.bufferSize = 1000;

  function Server(id, label) {
    this.id             = id;
    this.label          = label;
    this.running        = serverService.isRunning(id);
    this.messages       = [];
    this.selector       = '#'+id+'-server-status';
    this.dir            = serverService.getDir(id);

    var command         = serverService.getStartCommand(id);
    this.command        = command.command + ' ' + command.args.join(' ');
    this.init();
  }
  Server.prototype = {
    init: function () {
      var self = this;
      this.onStart = function () {
        self.status('MofoWallet Server Started');
        $timeout(function () {
          self.running = true;
        });
      };
      this.onExit = function () {
        self.status('MofoWallet Server Stopped');
        $timeout(function () {
          self.running = false;
        });
      };
      this.onUpdate = function () {
        self.update();
      }
      
      serverService.addListener(this.id, 'start', this.onStart);
      serverService.addListener(this.id, 'exit', this.onExit);
      serverService.addListener(this.id, 'stdout', this.onUpdate);  
      serverService.addListener(this.id, 'stderr', this.onUpdate); 

      $scope.$on('destroy', function () {
        serverService.removeListener(self.id, 'start', self.onStart);
        serverService.removeListener(self.id, 'exit', self.onExit);
        serverService.removeListener(self.id, 'stdout', self.onUpdate);  
        serverService.removeListener(self.id, 'stderr', self.onUpdate); 
      });

      function update() {
        if ($(self.selector).length == 0) {
          $timeout(update, 100);
        }
      }
      update();
    },
    start: function () {
      this.status('MofoWallet Server Starting');
      serverService.startServer(this.id);
    },
    stop: function () {
      this.status('MofoWallet Server Stopping');
      serverService.stopServer(this.id); 
    },
    isRunning: function () {
      return serverService.isRunning(this.id);
    },
    update: function () {
      var messages  = serverService.getMessages(this.id);
      var index     = (messages.length > $scope.bufferSize) ? 0 - $scope.bufferSize : 0;
      this.messages = messages.slice(index);
      this.setConsoleText(this.messages.join('\n'));
    },
    status: function (msg) {
      this.messages.push(msg);
      var index     = (this.messages.length > $scope.bufferSize) ? 0 - $scope.bufferSize : 0;
      this.messages = this.messages.slice(index);
      this.setConsoleText(this.messages.join('\n'));
    },
    setConsoleText: function (text) {
      var textarea = $(this.selector);
      if (textarea && textarea[0]) {
        textarea.val(text);
        textarea.scrollTop(textarea[0].scrollHeight);
      }
    }
  };

  /* @dependency on nxt.js this must match the TYPE_FIM and TYPE_NXT constant values */
  $scope.server = {
    TYPE_NXT: new Server('TYPE_NXT', 'NXT Server'),
    TYPE_FIM: new Server('TYPE_FIM', 'FIMK Server')
  };

  $scope.selectedTabChanged = function (id) {
    $scope.server[id].update();
  }

  $timeout(function () {
    $scope.server.TYPE_NXT.update();
    $scope.server.TYPE_FIM.update();
  }, 500);

});
})();
(function () {
'use strict';
var module = angular.module('fim.base');
module.run(function (plugins, modals, $q, $timeout, db, nxt) {  

  /* Global variables hidden from outside code | rely on the fact that 
     there is one wallet opened each time */
  var gPassword = '';
  var gFile     = '';
  var gWallet   = null;
  var gDeferred = null;

  /* Register as plugin */
  plugins.register({
    id:       'wallet',
    label:    'Wallet',
    extends:  'settings',
    icon_class:   'glyphicon glyphicon-briefcase',
    templateURL:  'plugins/wallet/partials/wallet-manager.html',    

    /* Returns the internal file object */
    getFile: function () {
      return gFile;
    },

    /* Returns a copy of the internal wallet object */
    getWallet: function () {
      return angular.copy(gWallet);
    },

    /* Returns all wallet entry keys */
    getKeys: function () {
      return gWallet ? Object.keys(gWallet) : [];
    },

    /* Do we have that key? */
    hasKey: function (key) {
      return gWallet ? !!gWallet[key] : false;
    },

    /* Remove an entry from the wallet */
    remove: function (key) {
      delete gWallet[key];
    },

    /* Returns the secretPhrase if any */
    getSecretPhrase: function (key) {
      return (gWallet && gWallet[key]) ? gWallet[key].secretPhrase : null;
    },

    /* Prompts the user to open a wallet */
    promptForWallet: function () {
      return promptForWallet();
    },

    /**
     * Returns an entry from the wallet. If no wallet is open the user is prompted to
     * select a wallet file and to provide his password to unlock it.
     *
     * @returns Promise
     */
    getEntry: function (id_rs) {
      var deferred    = $q.defer();

      /* Wallet is open and key is available */
      if (gWallet && _getEntry(id_rs)) {
        deferred.resolve(_getEntry(id_rs));
      }
      else {
        if (gWallet) {
          plugins.get('alerts').warn({ message: 'Key not found in wallet. Close dialog to select another wallet.'}).then(
            function () {
              promptForWallet().then(
                function () {
                  // recursive - try again now with other wallet
                  plugins.get('wallet').getEntry(id_rs).then(deferred.resolve, deferred.reject);
                },
                function () {
                  deferred.reject();
                }
              );
            }
          );
        }
        else {
          promptForWallet().then(
            function () {
              // recursive - try again now with other wallet
              plugins.get('wallet').getEntry(id_rs).then(deferred.resolve, deferred.reject);
            },
            function () {
              deferred.reject();
            }
          );
        }
      }
      return deferred.promise;
    },

    /**
     * Ads a new entry to the wallet and prompts the user to save it.
     * Returning a Promise is useless since we dont know when the saveas dialog completes.
     */
    addEntry: function (entry) {
      var self = this;
      var deferred = $q.defer();

      if (validateEntry(entry)) {
        if (gWallet) {
          gWallet[entry.id_rs] = entry;
          this.save(gFile.name, gPassword).then(deferred.resolve, deferred.reject);
        }
        else {
          plugins.get('alerts').confirm({ message: 'Do you want to store this secretphrase in an existing wallet?' }).then(
            function (value) {
              if (value) {
                promptForWallet().then(
                  function () {
                    // recursive - try again now with other wallet
                    plugins.get('wallet').addEntry(entry);
                  },
                  deferred.reject
                );
              }
              else {
                gWallet = {};
                gWallet[entry.id_rs] = entry;
                self.save().then(deferred.resolve, deferred.reject);
              }
            }
          );
        }
      }
      else {
        console.log('Invalid entry !!', entry);
        deferred.reject();
      }
      return deferred.promise;
    },

    /**
     * Creates a new Promise ($q.defer()) which is stored in the global gDeferred
     * variable. When the wallet is opened and decrypted in window.onWalletFileSelected
     * if the gDeferred variable is set it is called with resolve or reject result.
     */
    createOnWalletFileSelectedPromise: function () {
      gDeferred = $q.defer();
      return gDeferred.promise;
    },

    /**
     * @returns Promise
     * */
    save: function (fileName, password) {
      if (gWallet === null) {
        throw new Error('No wallet opened, cannot save.')
      }

      var deferred    = $q.defer();
      saveWallet(password).then(
        function (items) {
          try {
            var serialized  = JSON.stringify(addPadding(gWallet));
            var encrypted   = encrypt(items.password, serialized);
            var blob        = new Blob([encrypted], {type: "text/plain;charset=utf-8"});
            saveAs(blob, fileName||'mofo.wallet');
          }
          catch (e) {
            deferred.reject(e);
          }
          $timeout(function () {
            deferred.resolve();
          }, 1000);
        },
        function () {
          deferred.reject();
        }
      );
      return deferred.promise;
    },

    /* Opens wallet manager */
    manager: function () {
      var deferred = $q.defer();
      modals.open('walletManager', {
        resolve: {
          items: function () {
            return {};
          }
        },
        close: function () {
          deferred.resolve();
        }
      });
      return deferred.promise;
    }
  });

  /* Register modal dialogs */
  modals.register('walletManager', { 
    templateUrl: 'plugins/wallet/partials/wallet-manager-modal.html', 
    controller: 'WalletManagerModalController' 
  });
  modals.register('walletPassword', { 
    templateUrl: 'plugins/wallet/partials/wallet-password-modal.html', 
    controller: 'WalletPasswordModalController' 
  });
  modals.register('walletOpen', { 
    templateUrl: 'plugins/wallet/partials/wallet-open-modal.html', 
    controller: 'WalletOpenModalController' 
  });
  modals.register('walletSave', { 
    templateUrl: 'plugins/wallet/partials/wallet-save-modal.html', 
    controller: 'WalletSaveModalController' 
  }); 

  /** 
   * Called from <input type="file" onchange="onWalletFileSelected(event)">
   * Prompts the user for a password, then decrypts the wallet.
   * You can register 
   */
  window.onWalletFileSelected = function (event) {
    gWallet     = null;
    gPassword   = null;
    gFile       = null;

    var selectedFile = event.target.files[0];
    var reader    = new FileReader();
    reader.onload = function(event) {
      var cipherText = event.target.result;
      promptForPassword(selectedFile, cipherText).then(
        function (password) {
          try {
            var text    = decrypt(password, cipherText);
            gWallet     = removePadding(JSON.parse(text));
            gPassword   = password;
            gFile       = selectedFile;

            /* Add all entries in the wallet to the accounts database */
            db.accounts.toArray().then(
              function (accounts) {
                var map = {};
                angular.forEach(accounts, function (a) { map[a.id_rs] = a} );

                db.transaction('rw', db.accounts, function () {
                  angular.forEach(gWallet, function (entry) {
                    if (!(entry.id_rs in map)) {
                      db.accounts.put({ 
                        id_rs: entry.id_rs, 
                        name: entry.name,
                        engine: nxt.get(entry.id_rs).type 
                      });
                    }
                  });
                });
              }
            );

            if (gDeferred) {
              gDeferred.resolve({password: password, file: selectedFile});
              gDeferred = null;
            }
          } 
          catch (e) {
            console.log('unlockWallet', e, e.stack);
            if (gDeferred) {
              gDeferred.reject();
              gDeferred = null;
            }
          }
        }
      ); 
    };
    reader.onerror = function (event) {
      if (gDeferred) {
        gDeferred.reject();
        gDeferred = null;
      }
    };
    reader.onabort = function (event) {
      if (gDeferred) {
        gDeferred.reject();
        gDeferred = null;
      }
    };
    reader.readAsText(selectedFile);    
  }

  function promptForPassword(file, cipherText) {
    var deferred = $q.defer();
    modals.open('walletPassword', {
      resolve: {
        items: function () {
          return {
            password: '',
            file: file,
            isPasswordCorrect: function (password) {
              try {                  
                decrypt(password, cipherText);
                return true;
              } 
              catch (e) {
                return false;
              }
            }
          };
        }
      },
      close: function (items) {
        deferred.resolve(items.password);
      },
      cancel: function () {
        deferred.reject();
      }
    });
    return deferred.promise;
  }

  /* Shows a modal with a `Open wallet` button once the user selected a wallet file he/she
     will be prompted for a password. If the decryption happens successfully the returned
     promise resolve is called otherwise reject is called.
     In the promise resolve the user can expect the wallet to be opened and decrypted. */
  function promptForWallet() {
    var deferred = $q.defer();
    modals.open('walletOpen', {
      resolve: {
        items: function () {
          return {};
        }
      },
      close: function () {
        deferred.resolve();
      },
      cancel: function () {
        deferred.reject();
      }
    });
    return deferred.promise;    
  }

  function saveWallet(file, password) {
    var deferred = $q.defer();
    modals.open('walletSave', {
      resolve: {
        items: function () {
          return {
            password: password || '',
            file:     file || ''
          };
        }
      },
      close: function (items) {
        deferred.resolve(items);
      },
      cancel: function () {
        deferred.reject();
      }
    });
    return deferred.promise;    
  }

  function _getEntry(id_rs) {
    return gWallet ? gWallet[id_rs] : null;
  }

});

var config   = {
  name:         String,
  id_rs:        String,
  secretPhrase: String
};
var configCount = Object.keys(config).length;

function validateEntry(entry) {
  var count = 0;
  for (var name in entry) {
    if (name in config) {
      if (new Object(entry[name]) instanceof config[name] && entry[name] !== undefined) {
        count++;
        continue;
      }
    }
    console.log('wallet.validateEntry Invalid entry for '+name, entry);
    return false;
  }
  return count == configCount;
}

// Formats and wraps the ciphertext, salt and iv as JSON document
var WalletFormatter = {
  stringify: function (cipherParams) {
    var jsonObj = {
      ct: cipherParams.ciphertext.toString(CryptoJS.enc.Base64)
    };
    if (cipherParams.iv) {
        jsonObj.iv = cipherParams.iv.toString();
    }
    if (cipherParams.salt) {
        jsonObj.s = cipherParams.salt.toString();
    }
    return JSON.stringify(jsonObj, undefined, 2);
  },

  parse: function (jsonStr) {
    var jsonObj = JSON.parse(jsonStr);
    var cipherParams = CryptoJS.lib.CipherParams.create({
        ciphertext: CryptoJS.enc.Base64.parse(jsonObj.ct)
    });
    if (jsonObj.iv) {
        cipherParams.iv = CryptoJS.enc.Hex.parse(jsonObj.iv)
    }
    if (jsonObj.s) {
        cipherParams.salt = CryptoJS.enc.Hex.parse(jsonObj.s)
    }
    return cipherParams;
  }
};

/**
 * @param passPhrase String
 * @param plaintext String
 * @returns String
 */
function encrypt(passPhrase, plainText) {
  if (!window.crypto && !window.msCrypto) {
    throw new Error('FIX ME !! Should use mouse initiated seed!!!'); // 
  }

  var passByte  = converters.stringToByteArray(passPhrase);
  var key       = CryptoJS.SHA256(converters.byteArrayToWordArray(passByte));
  var tmp       = new Uint8Array(16);

  if (window.crypto) {
    window.crypto.getRandomValues(tmp);
  } else {
    window.msCrypto.getRandomValues(tmp);
  }

  var iv        = converters.byteArrayToWordArray(tmp);
  var encrypted = CryptoJS.AES.encrypt(plainText, key.toString(CryptoJS.enc.Hex), { 
    iv: iv,
    format: WalletFormatter
  });
  return encrypted.toString();
}

/**
 * @param password String
 * @param cypherText String
 * @returns String
 */
function decrypt(passPhrase, cipherText) {
  var passByte  = converters.stringToByteArray(passPhrase);
  var key       = CryptoJS.SHA256(converters.byteArrayToWordArray(passByte));
  var decrypted = CryptoJS.AES.decrypt(cipherText, key.toString(CryptoJS.enc.Hex), { 
    format: WalletFormatter 
  });
  return decrypted.toString(CryptoJS.enc.Utf8);
}

function addPadding(obj) {
  obj = angular.copy(obj);
  obj.padding = new Array(20000).join('ABCD');
  return obj;
}

function removePadding(obj) {
  delete obj.padding;
  return angular.copy(obj);
}

})();
(function () {
'use strict';
var module = angular.module('fim.base');
module.controller('SettingsPluginWalletController', function($scope, $timeout, plugins, db, alerts, nxt, $sce) {

  var walletPlugin  = plugins.get('wallet');
  $scope.fileName   = '';

  function load() {
    $scope.entries  = [];
    $scope.wallet   = walletPlugin.getWallet();    
    $scope.file     = walletPlugin.getFile();    
    $scope.label    = formatFile($scope.file);  
    $scope.fileName = ($scope.file && $scope.file.name) || '';

    angular.forEach(walletPlugin.getKeys(), function (key) {
      var entry = $scope.wallet[key];
      $scope.entries.push({
        name:   entry.name,
        id_rs:  entry.id_rs
      });
    });
  }

  load();

  plugins.get('wallet').createOnWalletFileSelectedPromise().then(
    function () { 
      $timeout(function () { 
        load();
      }); 
    },
    function () { /* Must provide feedback here */ }
  );

  function bytesToSize(bytes) {
     if(bytes == 0) return '0 Byte';
     var k = 1000;
     var sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
     var i = Math.floor(Math.log(bytes) / Math.log(k));
     return (bytes / Math.pow(k, i)).toPrecision(3) + ' ' + sizes[i];
  }

  function formatFile(file) {
    return file ? ('File: ' + file.name + ' | ' + bytesToSize(file.size) + ' | last modified: ' + $.timeago(file.lastModifiedDate)) : '';
  }  

  /* XXX TODO investigate if using a modal window will help to detect native save as to complete
              it could be that a closed modal dialog does not return until you closed all open
              save as dialogs... possibly hopefully.. 
              now there is no way to detect succesfull native save as */
  $scope.backupWallet = function () {
    walletPlugin.save($scope.fileName);
  }

  $scope.showSecretPhrase = function (key) {
    var entry = $scope.wallet[key];
    if (entry) {
      plugins.get('alerts').info({
        title: 'Secretphrase for ' + entry.id_rs,
        message: entry.secretPhrase
      });
    }
  }

  $scope.removeSecretPhrase = function (key) {
    var entry = $scope.wallet[key];
    if (entry) {
      plugins.get('alerts').confirm({ 
        html: $sce.trustAsHtml('<p>Please confirm that you want to remove this account from your wallet.<br>'+
              'You must save/backup your wallet for the removal to take effect.</p>')
      }).then(
        function (remove_from_wallet) {
          if (remove_from_wallet) {
            $scope.$evalAsync(function () {
              walletPlugin.remove(key);
              plugins.get('alerts').confirm({ 
                html: $sce.trustAsHtml('<p>Do you want to remove this account from mofo\'s database too?<br>'+
                        'If you choose not to remove the account from the database you can remove it later from within the accounts section.</p>')
              }).then(
                function (remove_from_db) {
                  if (remove_from_db) {
                    db.accounts.delete(key).then(
                      function () {
                        load();
                      }
                    );
                  }
                  else {
                    load();
                  }
                }
              );
            });
          }
        }
      );
    }
  }

});
})();
