// Original: https://github.com/doukasd/AngularJS-Components/
//
// a directive to auto-collapse long text
// in elements with the "dd-text-collapse" attribute
var module = angular.module('fim.base');
module.directive('ddTextCollapse', ['$compile', function($compile) {

    return {
        restrict: 'A',
        scope: true,
        link: function(scope, element, attrs) {

            // start collapsed
            scope.collapsed = false;

            // create the function to toggle the collapse
            scope.toggle = function() {
                scope.collapsed = !scope.collapsed;
            };

            // wait for changes on the text
            attrs.$observe('ddTextCollapseText', function(text) {

                // get the length from the attributes
                var maxLength = scope.$eval(attrs.ddTextCollapseMaxLength);

                if (text.length > maxLength) {
                    // split the text in two parts, the first always showing
                    var firstPart = String(text).substring(0, maxLength);
                    var secondPart = String(text).substring(maxLength, text.length);

                    // create some new html elements to hold the separate info
                    var firstSpan = $compile('<span>' + firstPart + '</span>')(scope);
                    var secondSpan = $compile('<span ng-if="collapsed">' + secondPart + '</span>')(scope);
                    var moreIndicatorSpan = $compile('<span ng-if="!collapsed">... </span>')(scope);
                    var lineBreak = $compile('<br ng-if="collapsed">')(scope);
                    var toggleButton = $compile('<a href class="collapse-text-toggle" ng-click="toggle()">{{collapsed ? "less" : "more"}}</a>')(scope);

                    // remove the current contents of the element
                    // and add the new ones we created
                    element.empty();
                    element.append(firstSpan);
                    element.append(secondSpan);
                    element.append(moreIndicatorSpan);
                    element.append(lineBreak);
                    element.append(toggleButton);
                }
                else {
                    element.empty();
                    element.append(text);
                }
            });
        }
    };
}]);
(function () {
	'use strict';
	var module = angular.module('fim.base');
	module.directive('routeActive', function($location, $rootScope) {
		return {
			link: function(scope, element, attrs) {
				var matcher 		= attrs.routeActive, 
					  activeClass = attrs.routeActiveClass || 'active',
					  matcherList = scope.$eval(attrs.routeActiveList);

				function normalize(path) {
					if (path[0] === '#') { path = path.substring(1); }
					if (path[0] === '/') { path = path.substring(1); }
					return path;
				}

				function update() {
					var path 		 = normalize($location.path()||'');
					var matchers = matcherList || [matcher];
					for (var i=0; i<matchers.length; i++) {
						if (matchers[i] && path.indexOf(matchers[i]) == 0) {
							element.addClass(activeClass);
							return;
						}
					}
					element.removeClass(activeClass);
				}

				$rootScope.$on('$locationChangeSuccess', function(event) {
					update();
				});

				attrs.$observe('routeActive', function (value) {
					matcher = value;
					update();
				});
				update();
			}
		}
	});

})();
